    比如：输入2，输出10，输入10，输出1010
    */
function format(num) {
    if (num == 1) {
        return '01';
    }
    var result = [];
    while (num >= 1) {
        if (num === 1) {
            result.unshift(1)
            return result.join('');
        }
        if (num % 2 === 1) {
            num -= 1;
            num /= 2;
            result.unshift(1)
        }
        if (num % 2 === 0) {
            num /= 2;
            result.unshift(0)
        }
    }
}
