// 存储函数
function memoize(fn, resolver = JSON.string, time) {
  const cache = new Map();

  return function (...args) {
    const key = resolver();

    const now = +new Date();

    if (cache.has(key)) {
      const valObj = cache.get(key);
      if (time) {
        cache.delete(key);
      } else {
        return valObj.value;
      }
    }

    const result = fn.apply(this, args);

    cache.set(key, { value: result, stmp: now });

    return result;
  };
}

