解释: 4没有超过3的两倍大, 所以我们返回 -1.
*/
function isTwice(list) {
    if (!Array.isArray(list)) {
        return -1;
    }
    if (list.length === 1) {
        return -1;
    }
    var newArr = [...list].sort((a,b) => b-a);
    var max = newArr[0];
    var second = newArr[1];
    if (max / second >= 2) {
        return list.findIndex(item => item === max);
    } else {
        return -1;
    }
    return newArr
}
var nums = [3, 6, 11, 0, 12];
isTwice(nums);
